sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
   var gr = new GlideRecord('em_alert');
   gr.get(input.sys_id);
   gs.include('EvtMgmtIncidentHandler');
   global.EvtMgmtIncidentHandler.createIncident(input, false);
   if (gr.incident.nil() && gr.incident == undefined) {
       gs.error("Failed to create incident");
       var errorMsg = gs.getMessage("Failed to create incident");
       gs.addErrorMessage(errorMsg);
   }
   else {
      input.incident = gr.incident;
      gr.update();
  }
})(input);
